import React, { Component } from  'react';
import api from '../../api.js'
class Conta extends Component{



    constructor(props){
        super(props);
        this.salario=""
        this.dependentes=""
        this.disconto=""
        this.teste="0"
    }
    handleSalario = (event) =>{
        this.salario = (event.target.value);
        console.log(this.salario);
    }
    handledependentes = (event) =>{
        this.dependentes = (event.target.value);
        console.log(this.dependentes);
    }
    handledisconto = (event) =>{
        this.disconto = (event.target.value);
        console.log(this.disconto);
    }


    enviarformulario = (event) =>{
      api.post('/calculo',this.salario, this.dependentes,this.disconto)}

      render(){
        return(
            <form
                className="inputs"
                onSubmit={this.enviarformulario.bind(this)}
            >
                <input
                  className="inp"
                  type="number"
                  placeholder="Salario"
                  min="1"
                  step=".01"
                  onChange={this.handleSalario.bind(this)}

                />
                 <input
                  className="inp"
                  type="number"
                  placeholder="Dependentes"
                  min="0"
                  step=".01"
                  onChange={this.handledependentes.bind(this)}

                />

                <input
                  className="inp"
                  type="number"
                  placeholder="Desconto"
                  min="0"
                  step=".01"
                  onChange={this.handledisconto.bind(this)}

                />

            <button className="calcbut">
                CALCULAR
            </button>
            </form>
        );
    }
}
